<?php 
return array (
  1 => 'ಸಾರ್ವಜನಿಕ ಡೊಮೇನ್ ಪುಸ್ತಕಗಳೊಂದಿಗೆ ಸಮಾನಾಂತರ / ದ್ವಿಭಾಷಾ ಟೈಪಿಂಗ್ ಪರೀಕ್ಷೆಗಳಿಗಾಗಿ ಒಂದು ವೆಬ್‌ಸೈಟ್.',
  2 => 'ಪುಸ್ತಕಗಳನ್ನು ಓದುವಾಗ ಟೈಪಿಂಗ್ ಅಭ್ಯಾಸ ಮಾಡಿ',
  3 => 'ಈ ವೆಬ್‌ಸೈಟ್‌ಗೆ ಸೇರಿಸಲಾದ ಯಾವುದೇ ಸಾರ್ವಜನಿಕ ಡೊಮೇನ್ ಪುಸ್ತಕವನ್ನು ಓದುವಾಗ ಟೈಪಿಂಗ್ ಅಭ್ಯಾಸ ಮಾಡಿ. ಈ ವೆಬ್‌ಸೈಟ್ ಅನ್ನು ಮೂಲತಃ ಭಾಷೆಗಳ ಕಲಿಕೆಗೆ ಸಹಾಯ ಮಾಡಲು ವಿನ್ಯಾಸಗೊಳಿಸಲಾಗಿದೆ, ಆದರೆ ಹೊಂದಿಕೊಳ್ಳುವ ಪುಸ್ತಕ ಟೈಪಿಂಗ್ ವೇದಿಕೆಯಾಗಿ ದ್ವಿಗುಣಗೊಳ್ಳುತ್ತದೆ. ಈ ವೆಬ್‌ಸೈಟ್ ಬಳಸುವ ಮೂಲಕ, ನಾವು ಬೆಂಬಲಿಸುವ ಯಾವುದೇ ಭಾಷೆಗಳಲ್ಲಿ ಬಳಕೆದಾರರು ಸೇರಿಸಿದ ಯಾವುದೇ ಸಾರ್ವಜನಿಕ ಡೊಮೇನ್ ಪುಸ್ತಕಕ್ಕೆ ನೀವು ಪ್ರವೇಶವನ್ನು ಹೊಂದಿರುತ್ತೀರಿ.',
  4 => 'ಈಗ ಟೈಪ್ ಮಾಡಲು ಪ್ರಾರಂಭಿಸಿ',
  5 => 'ಭಾಷೆಯನ್ನು ಕಲಿಯುವಾಗ ಟೈಪಿಂಗ್ ಅಭ್ಯಾಸ ಮಾಡಿ',
  6 => 'ಭಾಷೆಯನ್ನು ಕಲಿಯುವ ಪ್ರಕ್ರಿಯೆಯಲ್ಲಿ ಇನ್ನೂ ಮುಂಚೆಯೇ ಆದರೆ ಟೈಪಿಂಗ್ ಅಭ್ಯಾಸ ಮಾಡಲು ಬಯಸುವಿರಾ? ನಾವು ಬೆಂಬಲಿಸುವ ಯಾವುದೇ 100+ ಭಾಷೆಗಳಲ್ಲಿ ನೀವು ಇಲ್ಲಿ ಟೈಪ್ ಮಾಡುವುದನ್ನು ಅಭ್ಯಾಸ ಮಾಡಬಹುದು',
  7 => 'ಅನುವಾದಗಳನ್ನು ತೋರಿಸುತ್ತಿರುವಾಗ.',
  8 => 'ನೀವು ಟೈಪ್ ಮಾಡುವಾಗ ನೀವು ಇನ್ನು ಮುಂದೆ ಅನುವಾದಗಳನ್ನು ಹುಡುಕುವ ಅಗತ್ಯವಿಲ್ಲ, ಏಕೆಂದರೆ ನೀವು ಟೈಪ್ ಮಾಡಿದಂತೆ ಅನುವಾದಗಳನ್ನು ಒದಗಿಸಲು ನಮ್ಮ ವೆಬ್‌ಸೈಟ್ ಅದೇ ಸೇವೆಗಳನ್ನು ಬಳಸುತ್ತದೆ.',
  9 => 'ನಿಮ್ಮ ಭಾಷೆಯನ್ನು ಆಯ್ಕೆಮಾಡಿ',
  10 => 'ನಿಮ್ಮ ಪ್ರಗತಿಯನ್ನು ಉಳಿಸಲಾಗಿದೆ',
  11 => 'ನೀವು ಪುಸ್ತಕಗಳ ಮೂಲಕ ಪ್ರಗತಿ ಸಾಧಿಸುತ್ತಿದ್ದಂತೆ, ನಿಮ್ಮ ಪ್ರಗತಿಯನ್ನು ನಿಮಗಾಗಿ ಉಳಿಸುತ್ತೇವೆ. ನೀವು ಎಲ್ಲಿಂದ ಹೊರಟು ಹೋಗಿದ್ದೀರಿ ಅಥವಾ ಇನ್ನೊಂದು ಪುಸ್ತಕವನ್ನು ಪ್ರಾರಂಭಿಸಲು ನೀವು ಯಾವಾಗ ಬೇಕಾದರೂ ಈ ವೆಬ್‌ಸೈಟ್‌ಗೆ ಹಿಂತಿರುಗಬಹುದು. ನಿಮ್ಮ ಪ್ರಗತಿ ಮತ್ತು ಕಾಲಾನಂತರದಲ್ಲಿ ನಿಮ್ಮ ಟೈಪಿಂಗ್ ಕೌಶಲ್ಯಗಳು ಹೇಗೆ ಸುಧಾರಿಸುತ್ತಿವೆ ಎಂಬುದು ನಿಮಗಾಗಿ ಉಳಿಸಲಾಗಿದೆ.',
  12 => 'ನಿಮ್ಮ ಅಂಕಿಅಂಶಗಳನ್ನು ಹಂಚಿಕೊಳ್ಳಿ',
  13 => 'ಸುಲಭವಾಗಿ ಹಂಚಿಕೊಳ್ಳಬಹುದಾದ ಪುಟಗಳೊಂದಿಗೆ ನಿಮ್ಮ ಪ್ರಗತಿಯನ್ನು ತೋರಿಸಿ. ಇಡೀ ಪುಸ್ತಕದಾದ್ಯಂತ ಯಾರು ವೇಗವಾಗಿ ಟೈಪಿಂಗ್ ವೇಗವನ್ನು ಸಾಧಿಸಬಹುದು ಎಂದು ನೋಡಲು ನೀವು ಸ್ನೇಹಿತರಿಗೆ ಸವಾಲು ಹಾಕಲು ಬಯಸಬಹುದು - ನೀವು ಅದನ್ನು ಇಲ್ಲಿ ಮಾಡಬಹುದು. ಅಥವಾ, ನೀವು ಸ್ಪರ್ಧಾತ್ಮಕವಾಗಿಲ್ಲದಿದ್ದರೆ, ನೀವು ಕಲಿಯುತ್ತಿರುವ ಎಲ್ಲಾ ಭಾಷೆಗಳಲ್ಲಿ ಅಥವಾ ನೀವು ಓದುತ್ತಿರುವ ಪುಸ್ತಕಗಳಲ್ಲಿ ನಿಮ್ಮ ಟೈಪಿಂಗ್ ಕೌಶಲ್ಯಗಳು ಹೇಗೆ ಸುಧಾರಿಸುತ್ತಿವೆ ಎಂಬುದನ್ನು ನೀವು ಸುಲಭವಾಗಿ ಬ್ರೌಸ್ ಮಾಡಬಹುದು.',
  14 => 'ಪುಸ್ತಕಗಳನ್ನು ಸುಲಭವಾಗಿ ಆಮದು ಮಾಡಿಕೊಳ್ಳಿ',
  15 => 'ನಿರ್ದಿಷ್ಟ ಪುಸ್ತಕವನ್ನು ಓದುವಾಗ ನೀವು ಎಂದಾದರೂ ಟೈಪಿಂಗ್ ಅಭ್ಯಾಸ ಮಾಡಲು ಬಯಸಿದರೆ, ಈ ವೆಬ್‌ಸೈಟ್ ನಿಮಗೆ ಹಾಗೆ ಮಾಡಲು ಅನುವು ಮಾಡಿಕೊಡುತ್ತದೆ. ಪುಸ್ತಕಗಳ ಸೂಚ್ಯಂಕವನ್ನು ಒಟ್ಟಿಗೆ ಇಟ್ಟುಕೊಂಡು ಈ ಪ್ರಕ್ರಿಯೆಯನ್ನು ಸಾಧ್ಯವಾದಷ್ಟು ಸುಲಭಗೊಳಿಸಲು ನಾವು ಪ್ರಯತ್ನಿಸಿದ್ದೇವೆ. ಯಾವುದೇ ಸಾರ್ವಜನಿಕ ಡೊಮೇನ್ ಪುಸ್ತಕವನ್ನು ತ್ವರಿತವಾಗಿ ಸೇರಿಸಿ ಮತ್ತು ಅಭ್ಯಾಸವನ್ನು ಪ್ರಾರಂಭಿಸಿ.',
  16 => 'ನೀವು ಸೇರಿಸುವ ಪುಸ್ತಕಗಳಲ್ಲಿನ ಸಮಸ್ಯೆಗಳನ್ನು ತ್ವರಿತವಾಗಿ ಸರಿಪಡಿಸಿ',
  17 => 'ನೀವು ಪುಸ್ತಕಗಳನ್ನು ಸೇರಿಸಲು ಬಯಸುತ್ತೀರಿ ಎಂದು ನೀವು ನಿರ್ಧರಿಸಿದ್ದರೆ, ಅವುಗಳನ್ನು ಟೈಪ್ ಮಾಡಬಹುದಾದ ಸ್ವರೂಪಕ್ಕೆ ಪರಿವರ್ತಿಸುವುದರಿಂದ ನಾವು ನೋವನ್ನು ಹೊರತೆಗೆಯಲು ಪ್ರಯತ್ನಿಸುತ್ತೇವೆ. ಪುಸ್ತಕಗಳ ಒಳಗಿನ ಎಲ್ಲಾ ಅಕ್ಷರಗಳನ್ನು ಸಾಮಾನ್ಯ ಕೀಬೋರ್ಡ್ ವಿನ್ಯಾಸಗಳ ವಿರುದ್ಧ ಪರಿಶೀಲಿಸಲಾಗುತ್ತದೆ - ಮತ್ತು ಆ ಕೀಗಳಿಗೆ ಹೊಂದಿಕೆಯಾಗದ ಯಾವುದೇ ಅಕ್ಷರಗಳನ್ನು ನೀವು ಸುಲಭವಾಗಿ ಸರಿಪಡಿಸಲು ಸ್ವಯಂಚಾಲಿತವಾಗಿ ಒದಗಿಸಲಾಗುತ್ತದೆ.',
  18 => 'ಡೇಟಾವನ್ನು ತೆರೆಯಿರಿ',
  19 => 'ನಮ್ಮ ವೆಬ್‌ಸೈಟ್‌ಗೆ ಸೇರಿಸಲಾದ ಪುಸ್ತಕಗಳು ಸಾರ್ವಜನಿಕ ಡೊಮೇನ್‌ನಲ್ಲಿವೆ, ಆದ್ದರಿಂದ ನಮ್ಮ ಬಳಕೆದಾರರು ಇಲ್ಲಿ ಸೇರಿಸುವ ಡೇಟಾದಿಂದ ನಾವು ಮಾತ್ರ ಪ್ರಯೋಜನ ಪಡೆಯಬೇಕು ಎಂಬುದು ನಮಗೆ ಸರಿಯಾಗಿ ಅನಿಸಲಿಲ್ಲ. ಎಲ್ಲಾ ಖಾಸಗಿ-ಅಲ್ಲದ ಪುಸ್ತಕಗಳು ಮತ್ತು ರಚಿಸಲಾದ ಅನುವಾದಗಳು ಯಾರಿಗಾದರೂ ಅವರು ಬಯಸಿದ ಯಾವುದೇ ಉದ್ದೇಶಕ್ಕಾಗಿ ಸುಲಭವಾಗಿ ಡೌನ್‌ಲೋಡ್ ಮಾಡಲು ಮತ್ತು ಬಳಸಲು ಲಭ್ಯವಾಗುವಂತೆ ಮಾಡಲಾಗುವುದು - ಅವುಗಳು ಸಾರ್ವಜನಿಕ ಡೊಮೇನ್.',
  20 => 'ಡಾರ್ಕ್ ಮತ್ತು ಲೈಟ್ ಮೋಡ್‌ಗಳು',
  21 => 'ತಡರಾತ್ರಿಯಲ್ಲಿ ಟೈಪಿಂಗ್ ಅಭ್ಯಾಸ ಮಾಡಲು ಬಯಸುವಿರಾ? ಕೇಂದ್ರೀಕರಿಸಲು ಪ್ರಕಾಶಮಾನವಾದ ಪರದೆಯ ಅಗತ್ಯವಿದೆಯೇ? ನಿಮ್ಮ ಆದ್ಯತೆಗಳಿಗೆ ಸರಿಹೊಂದುವಂತೆ ನಾವು ಡಾರ್ಕ್ ಮತ್ತು ಲೈಟ್ ಮೋಡ್‌ಗಳನ್ನು ಹೊಂದಿದ್ದೇವೆ. ಟೈಪಿಂಗ್ ಪರೀಕ್ಷೆಗಳ ಫಾಂಟ್ ಅನ್ನು ಸಹ ನೀವು ಬದಲಾಯಿಸಬಹುದು - ಟೈಪಿಂಗ್ ಪರೀಕ್ಷೆಗಳನ್ನು ಇಲ್ಲಿ ತೆಗೆದುಕೊಳ್ಳಲು ನಾವು ಆರಾಮದಾಯಕವಾಗಿಸಲು ಪ್ರಯತ್ನಿಸಿದ್ದೇವೆ.',
);